package com.hsd.fsxasm.activity;import android.view.View;import android.view.View.OnClickListener;import android.widget.EditText;import android.widget.RelativeLayout;import android.widget.Toast;import com.hsd.fsxasm.R;import com.hsd.fsxasm.base.BaseActivity;import com.hsd.fsxasm.domain.BaseBean;import com.hsd.fsxasm.domain.FeedBackBean;import com.hsd.fsxasm.engine.NFeedBackBeat;import com.hsd.fsxasm.global.TempInfo;public class FeedBackActivity extends BaseActivity{	private EditText editText;	private RelativeLayout ok;	@Override	protected View initView() {		View parent = View.inflate(mActivity, R.layout.il_feedback, null);		editText = (EditText) parent.findViewById(R.id.il_feedback_text);		ok = (RelativeLayout) parent.findViewById(R.id.il_feedback_ok);		return parent;	}	@Override	protected void initData() {		mTitle.setText("反馈建议");		mBack.setVisibility(View.VISIBLE);			}	@Override	protected void setListener() {		mBack.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				finish();			}		});		ok.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				if(editText.getText().toString().equals("")){					Toast.makeText(mActivity, "请填写反馈信息", 0).show();				}else{					FeedBackBean feedBackBean = new FeedBackBean();					feedBackBean.setFeedBack_Information(editText.getText().toString());					feedBackBean.setUUID(TempInfo.findFirst.getUuid());					NFeedBackBeat nFeedBackBeat = new NFeedBackBeat(mActivity, new HttpResponseHandler());					nFeedBackBeat.feedBack(feedBackBean);				}			}		});	}	@Override	protected void updataUI(Object obj) {		BaseBean bean = (BaseBean) obj;		if(bean.getResultCode()==1){			Toast.makeText(mActivity, "提交成功", 0).show();			finish();		}else{			Toast.makeText(mActivity, "提交失败", 0).show();		}	}}