package com.hsd.fsxasm.activity;import java.text.SimpleDateFormat;import android.view.View;import android.view.View.OnClickListener;import android.widget.TextView;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.R;import com.hsd.fsxasm.base.BaseActivity;import com.hsd.fsxasm.domain.UserInformationBean;public class FriendsOtherPersonInfoActivity extends BaseActivity {	private TextView nameText;	private TextView sexText;	private TextView birthdayText;	private TextView starText;	private TextView heightText;	private TextView signText;	private TextView idText;	private TextView schoolText;	private TextView departText;	private TextView homeText;	private TextView affectText;		private String name;	private String sex;	private String birthday;	private String star;	private String heigh;	private String sign;	private String id;	private String school;	private String depart;	private String home;	private String affect;		private UserInformationBean userInformationBean;		@Override	protected View initView() {		View parent = View.inflate(this, R.layout.il_friendsotherpersoninfo, null);		nameText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_nickname);		sexText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_gender);		birthdayText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_brithday);		starText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_constellation);		heightText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_height);		signText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_signature);		idText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_id);		schoolText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_school);		departText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_department);		homeText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_location);		affectText = (TextView) parent.findViewById(R.id.id_foufri_otherpersoninfo_affectivestate);				userInformationBean = JSONObject.parseObject(getIntent().getStringExtra("userinformationbean"), UserInformationBean.class);						return parent;	}	@Override	protected void updataUI(Object obj) {			}	@Override	protected void initData() {		mBack.setVisibility(View.VISIBLE);		mTitle.setText("详细信息");		mHelp.setVisibility(View.GONE);				name = userInformationBean.getUser_nickname();		sex = userInformationBean.getUser_sex();		SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd");		birthday = dateFormat.format(userInformationBean.getUser_birthday()).toString();		star = userInformationBean.getUser_star();		heigh = userInformationBean.getUser_height()+"";		sign = userInformationBean.getUser_sign();		id = userInformationBean.getUserInformation_ID()+"";		school = userInformationBean.getUser_school();		depart = userInformationBean.getUser_department();		home = userInformationBean.getUser_locality();		affect = userInformationBean.getUser_state();						nameText.setText(name);		sexText.setText(sex);		birthdayText.setText(birthday);		starText.setText(star);		heightText.setText(heigh);		signText.setText(sign);		idText.setText(id);		schoolText.setText(school);		departText.setText(depart);		homeText.setText(home);		affectText.setText(affect);	}	@Override	protected void setListener() {		mBack.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				finish();			}		});	}}