package com.hsd.fsxasm.db;import org.xutils.db.annotation.Column;import org.xutils.db.annotation.Table;@Table(name = "userimage")public class MDbName {		@Column(name = "id" , isId = true)	private int id;  			@Column(name = "user_id")	private String user_id;		@Column(name = "username")	private String username;		@Column(name = "pic")	private String pic;			public String getUser_id() {		return user_id;	}	public void setUser_id(String user_id) {		this.user_id = user_id;	}	public String getUsername() {		return username;	}	public void setUsername(String username) {		this.username = username;	}	public String getPic() {		return pic;	}	public void setPic(String pic) {		this.pic = pic;	}		}