package com.hsd.fsxasm.params;import org.xutils.http.RequestParams;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.domain.FriendCircleBean;import com.hsd.fsxasm.domain.HeartBeatBean;import com.hsd.fsxasm.global.Global;import com.hsd.fsxasm.global.TempInfo;import android.R.integer;import android.app.DownloadManager.Request;public class AddHeartBeatParams extends RequestParams{	public AddHeartBeatParams(Integer userID) {		super(Global.ADD_HeartBeat);		this.setAsJsonContent(true);		HeartBeatBean bean = new HeartBeatBean();		bean.setUUID(TempInfo.findFirst.getUuid());		bean.setByHeartBeatID(userID);				this.addBodyParameter("json",JSONObject.toJSONString(bean));	}	}