package com.hsd.fsxasm.params;import org.xutils.http.RequestParams;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.domain.CertificationBean;import com.hsd.fsxasm.domain.LoginBean;import com.hsd.fsxasm.global.Global;public class CertificationParams extends RequestParams{	public CertificationParams(String username,String pwd,String schoolName) {		super(Global.CHECK_STUDENT);						this.setAsJsonContent(true);				CertificationBean bean = new CertificationBean();		bean.setStudent_number(username);		bean.setStudent_password(pwd);		bean.setStudent_schoolName(schoolName);		this.addBodyParameter("json",JSONObject.toJSONString(bean));	}	}