package com.hsd.fsxasm.params;import org.xutils.http.RequestParams;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.domain.CertificationBean;import com.hsd.fsxasm.domain.FindFriendsBean;import com.hsd.fsxasm.global.Global;import com.hsd.fsxasm.utils.LogUtil;public class CheckSchoolParams extends RequestParams{	public CheckSchoolParams(String schoolName) {		super(Global.GET_MODLE);		this.setAsJsonContent(true);		this.setCharset("UTF-8");				CertificationBean bean = new CertificationBean();		bean.setStudent_schoolName(schoolName);				this.addBodyParameter("json", JSONObject.toJSONString(bean));	}}