package com.hsd.fsxasm.params;import org.xutils.http.RequestParams;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.domain.FriendCircleBean;import com.hsd.fsxasm.global.Global;public class FriendCircleParams extends RequestParams{	public FriendCircleParams(String uuid , int pageNow,int pageSize) {		super(Global.GET_SAIDS);		this.setAsJsonContent(true);				FriendCircleBean bean = new FriendCircleBean();		bean.setUUID(uuid);		bean.setFriendsCircle_pageNow(pageNow);		bean.setFriendsCircle_pageSize(80);				this.addBodyParameter("json",JSONObject.toJSONString(bean));	}	}