package com.hsd.fsxasm.params;import org.xutils.http.RequestParams;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.domain.FriendCircleBean;import com.hsd.fsxasm.domain.HeartBeatBean;import com.hsd.fsxasm.domain.UserInformationBean;import com.hsd.fsxasm.global.Global;import com.hsd.fsxasm.global.TempInfo;import android.R.integer;import android.app.DownloadManager.Request;public class GetUserInfoParams extends RequestParams{	public GetUserInfoParams(Integer userID) {		super(Global.GET_UserInfo);		this.setAsJsonContent(true);		UserInformationBean bean = new UserInformationBean();		bean.setUUID(TempInfo.findFirst.getUuid());		bean.setUserInformation_ID(userID);				this.addBodyParameter("json",JSONObject.toJSONString(bean));	}	}