package com.hsd.fsxasm.params;import org.xutils.http.RequestParams;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.domain.AddGoodBean;import com.hsd.fsxasm.domain.LoginBean;import com.hsd.fsxasm.global.Global;import com.hsd.fsxasm.global.TempInfo;public class GoodParams extends RequestParams{	public GoodParams(Integer friendCircle_id) {		super(Global.ADD_LIKE);						this.setAsJsonContent(true);				AddGoodBean bean = new AddGoodBean();				bean.setUUID(TempInfo.findFirst.getUuid());		bean.setFriendsCircle_ID(friendCircle_id);				this.addBodyParameter("json",JSONObject.toJSONString(bean));						}	}