package com.hsd.fsxasm.params;import org.xutils.http.RequestParams;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.domain.FriendCircleBean;import com.hsd.fsxasm.domain.HeartBeatBean;import com.hsd.fsxasm.global.Global;import com.hsd.fsxasm.global.TempInfo;import android.app.DownloadManager.Request;public class HeartBeatParams extends RequestParams{	public HeartBeatParams() {		super(Global.GET_SAIDS);		this.setAsJsonContent(true);				HeartBeatBean bean = new HeartBeatBean();		bean.setUUID(TempInfo.findFirst.getUuid());				this.addBodyParameter("json",JSONObject.toJSONString(bean));	}	}