package com.hsd.fsxasm.params;import org.xutils.http.RequestParams;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.domain.LoginBean;import com.hsd.fsxasm.domain.RegisterBean;import com.hsd.fsxasm.global.Global;public class RegisterParams extends RequestParams{	public RegisterParams(String username,String pwd,String student_number) {		super(Global.REGISTER);				this.setAsJsonContent(true);				RegisterBean loginBean = new RegisterBean();		loginBean.setUser_phone(username);		loginBean.setUser_password(pwd);		loginBean.setStudent_ID(student_number);		this.addBodyParameter("json",JSONObject.toJSONString(loginBean));			}		}