package com.hsd.fsxasm.params;import org.xutils.http.RequestParams;import com.alibaba.fastjson.JSONObject;import com.hsd.fsxasm.domain.UserInformationBean;import com.hsd.fsxasm.global.Global;public class UpDateUserInfoParams extends RequestParams {		private UserInformationBean userInformationBean;	public UpDateUserInfoParams(UserInformationBean userInformationBean){		super(Global.UPLOAD_INFO);		this.setAsJsonContent(true);		this.userInformationBean = userInformationBean;		if(userInformationBean!=null){			this.addBodyParameter("json", JSONObject.toJSONString(userInformationBean));		}	}}