package com.hsd.fsxasm.utils;import android.util.Log;/** * Log显示工具类 */public class LogUtil {	/**	 * 开发	 */	private final static int DEVELOP = 0;	/**	 * 测试	 */	private final static int TEST = 1;	/**	 * 上线	 */	private final static int ONLINE = 2;	/**	 * 当前状态	 */	private final static int NOWSTATE = DEVELOP;	public static void info(String msg) {		info("volunteer", msg);	}	public static void info(String TAG, String msg) {		switch (NOWSTATE) {		case DEVELOP:			Log.i(TAG, "develop:---" + msg);			break;		case TEST:			Log.i(TAG, "test:---" + msg);			break;		case ONLINE:			break;		}	}}